using System;
using System.Net;
using System.Net.Mail;
using System.Threading.Tasks;

namespace AsyenServiceListen.Classes
{
    internal static class EMailSenderManager
    {
        internal static async Task MailSendForm(string companyName, string sender, string recipient, string password, string smtp, int port, bool ssl)
        {
            try
            {
                string logo_base64 = ""; 
                string subject = $"{companyName} - Servis DURDU!";
                string htmlBody = $@"
<html>
  <body style='font-family: Segoe UI, sans-serif; font-size: 14px; color: #333;'>
    <h2 style='color: #B22222;'>❌ Servis Durduruldu</h2>
    <p><strong>{companyName}</strong> firmasının veritabanı yedekleme servisi çalışmıyor.</p>
    <p>Lütfen en kısa sürede kontrol sağlayınız.</p>
    <hr />
    <footer style='font-size: 13px; color: #555;'>
      <p><strong>İletişim Bilgileri</strong></p>
      <p>Cumhuriyet Mah. D-100 Karayolu Cad. Adm Konaklama Sitesi Outlet Park Avm Blok No:371/63<br/>
         Büyükçekmece / İstanbul</p>
      <p><strong>Telefon:</strong> 0850 474 13 50<br/>
         <strong>E-posta:</strong> satis@asyen.com.tr</p>
      <img src='data:image/png;base64,{logo_base64}' alt='Firma Logosu' style='margin-top:20px; width:150px;' />
      <p style='font-size: 12px; color: #999; margin-top: 15px;'>Bu bir otomatik e-postadır, lütfen yanıt vermeyiniz.</p>
    </footer>
  </body>
</html>";
                using (MailMessage mail = new MailMessage(sender, recipient, subject, htmlBody))
                {
                    mail.IsBodyHtml = true;
                    using (SmtpClient client = new SmtpClient(smtp, port))
                    {
                        client.EnableSsl = ssl;
                        client.Credentials = new NetworkCredential(sender, password);
                        await client.SendMailAsync(mail);
                    }
                }
            }
            catch (Exception ex)
            {
                TextLog.TextLogging($"[MailSendForm Error] {ex}");
            }
        }
    }
}